ter-view :key="$route.fullPath"></router-view>                                    </div>                                </div>                            </div>                            </li>                        </ul>                </nav></template><script >    import _ from 'lodash';export default {    name:"Sidebar",    data() {        return {        };    },    created() {        this.$store.dispatch('loadSources', {apiBase:this.$env.apiBaseUrl});    },    computed: {        orderedUsers: function () {            return _.orderBy(this.sources, 'type')        },        sources() {            return this.$store.state.sources;        },    },    methods:{        deleteAction(id){            this.$emit("delete", id)        }    }}</script><style lang="scss" scoped>    @import '../styles/bootstrap_theme';.wrapper {    display: flex;    align-items: stretch;}    .dropdown-toggle {        background-color: #303030;        border: 1px solid #444;        display: flex;        justify-content: center;    }#sidebar {    min-width: 250px;    max-width: 250px;}    .list-group {        .facet-header-item {            border-left: solid $primary;        }        .facet-title {            font-weight: 600;            font-size: 1.1rem;        }    }#sidebar.active {    margin-left: -250px;}    li a {        text-decoration: none;        color: white;    }    li a:hover, li a:active,li a:focus {        color:#3498DB;    }button {         background-color: Transparent;    background-repeat:no-repeat;    border: none;    cursor:pointer;    overflow: hidden;        }</style>